// src/components/ProductBarChart.js
import React from 'react';
import { Bar } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    BarElement,
    Title,
    Tooltip,
    Legend
} from 'chart.js';
import './Style.css';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const ProductBarChart = ({ products }) => {
    const data = {
        labels: products.map(product => product.name),
        datasets: [
            {
                label: 'Product Quantity',
                data: products.map(product => product.quantity),
                backgroundColor: 'var(--bar-background-color)',
                borderColor: 'var(--bar-border-color)',
                borderWidth: parseInt(getComputedStyle(document.documentElement).getPropertyValue('--bar-border-width')),
            },
        ],
    };

    const options = {
        scales: {
            y: {
                beginAtZero: true,
            },
        },
    };

    return <Bar data={data} options={options} />;
};

export default ProductBarChart;
